import mailgun from 'mailgun-js';
const DOMAIN = process.env.mailgun_API_base_URL;
const api_key = process.env.mailgun_apiKey;

const mg = mailgun({ apiKey: api_key, domain: DOMAIN });

const sendMail = (email, subject) => {
	const data = {
		from: 'admin@zuby.io',
		to: email,
		subject: ' Thank you',
		text: 'I have recieved your message and I will urgently attend to it, Thanks for stopping by🤝'
	};

	mg.messages().send(data, (error, body) => {
		if (body) {
			console.log(body);
		}
		console.log(error);
	});
};

export default sendMail;
