import React, {useState} from 'react';
import Navbar from '../../components/Navbar/Navbar';
import Introduction from '../../components/Introduction/index';
import Skills from '../../components/Skills/index';
import Footer from '../../../'
import img3 from '../../assests/smile.jpg';
import { Tabs, Tab } from 'react-bootstrap';

const About = () => {
    const [intro, setIntro] = useState(true)
    const [skills, setSkills] = useState(false)
    const [exp, setExp] = useState(false)

    const handleShowIntro = () => {
        setIntro(true)
        setSkills(false)
        setExp(false)
    }

    const handleShowSkills = () => {
        setIntro(false)
        setSkills(true)
        setExp(false)
    }
    const handleShowExp = () => {
        setIntro(false)
        setSkills(false)
        setExp(true)
    }

	return (
		<div>
			<Navbar />
			<span />
			<span />
			<span />
			<span />

			<div className="section">
				<div className="container">
					<div className="level">About Me</div>
					<div className="columns">
						<div className="column is-4">
							<div className="container">
								<img src={img3} alt="zuby.io" />
							</div>
						</div>
						<div className="column is-8">
							<Tabs defaultActiveKey="intro" id="uncontrolled-tab-example">
								<Tab eventKey="intro" title="Introduction" onClick={handleShowIntro}>
									{intro && <Introduction />}
								</Tab>
								<Tab eventKey="skills" title="Skills" onClick={handleShowSkills}>
									{skills && <Skills />}
								</Tab>
								<Tab eventKey="experience" title="Experience" onClick={handleShowExp}>

                                </Tab>
							</Tabs>
						</div>
					</div>
				</div>
			</div>

		</div>
	);
}

export default About;
