import React, { useState } from 'react';
import ParticlesComponent from '../Particles/Particles';
import Button from '../Button/Button';
import quality from '../../assests/data';
import useInterval from '../../components/hooks/useInterval';
import _ from 'underscore';

export default function LandingPage(props) {
	const [ qualities, setQualities ] = useState([ 'Passion', 'Excellence', 'Determination', 'Innovation' ]);

    while(true){

    }
	const filterQualityArray = () => {
		const filteredQualityArray = _.sample(quality, 4);

		setQualities(filteredQualityArray);
	};

	// useInterval(() => {
	// 	filterQualityArray();
	// }, 1000);

	return (
		<div className="wrapper">
			<div className="container">
				<div className="title logo-text">
					<h1>Zuby.io</h1>
				</div>
			</div>
			<div className="skills has-text-centered">
            <h2 className="title has-text-weight-bold">
				{qualities.map((quality, i) => {
					console.log(quality);
					return (

							<strong>
								<span>{quality}</span>

							</strong>

					);
				})}

                </h2>
			</div>

			<div className="bg">
				<ParticlesComponent />

				<div className="centered">
					<Button handleClick={props.handleClick} />
				</div>
			</div>

			<div />
		</div>
	);
}
